import React from "react";
import Separator from "../components/Separator";
import Title from "../components/Title";
import ConsejoView from "../components/ConsejoView";
import styled from "styled-components";
import Collapsible from "./Collapsible";

const Reference = styled.p`
  display: block;
  width: 100%;
  font-size: 12px;
  color: var(--dark-grey);
  text-decoration: none;
  font-style: italic;

  @media screen and (min-width: 1024px) {
    font-size: 16px;
  }
`;

const TableContainer = styled.div`
  margin-top: 20px;
`;

const Table = styled.table`
  width: 100%;
  border-spacing: 0;

  th,
  td {
    padding: 0px;
    text-align: left;
    border-bottom: 1px solid #ddd;
  }

  th {
    background-color: #f2f2f2;
    font-weight: bold;
  }

  tr:hover {
    background-color: #f5f5f5;
  }
`;

const Th = styled.th`
  background-color: var(--light-grey);
  border: 1px solid var(--grey);
  padding: 8px;
  text-align: left;
`;

const Td = styled.td`
  border: 1px solid var(--grey);
  padding: 8px;
`;

const CollapsibleContent = styled.div`
  display: ${({ isOpen }) => (isOpen ? "block" : "none")};
`;

const CollapsibleButton = styled.button`
  background-color: transparent;
  border: none;
  cursor: pointer;
  color: var(--dark-grey);
  font-size: 14px;
  text-decoration: underline;
  margin-top: 8px;

  &:focus {
    outline: none;
  }
`;

const ConsejosEstatales = () => {
  const [isCollapsibleOpen, setIsCollapsibleOpen] = React.useState(false);

  const toggleCollapsible = () => {
    setIsCollapsibleOpen(!isCollapsibleOpen);
  };

  return (
    <>
      <Title isHeader>Consejos Estatales Forestales</Title>
      <Separator />
      <Reference>
        Seleccione el estado del que desee visualizar información.
      </Reference>
      <ConsejoView />

      <Collapsible title={"Informe de acuerdos"}>
        <Collapsible title={"2023"}>
          <CollapsibleContent isOpen={true}>
            <TableContainer>
              <Table>
                <thead>
                  <tr>
                    <Th>Trimestre</Th>
                    <Th>Documento</Th>
                  </tr>
                </thead>
                <tbody>
                  {/* Aquí puedes mapear los datos del JSON para 2023 */}
                  <tr>
                    <Td>Dato 1</Td>
                    <Td>Dato 2</Td>
                  </tr>
                </tbody>
              </Table>
            </TableContainer>
          </CollapsibleContent>
        </Collapsible>
        <Collapsible title={"2024"}>
          <CollapsibleContent isOpen={true}>
            <TableContainer>
              <Table>
                <thead>
                  <tr>
                    <Th>Columna 1</Th>
                    <Th>Columna 2</Th>
                  </tr>
                </thead>
                <tbody>
                  {/* Aquí puedes mapear los datos del JSON para 2024 */}
                  <tr>
                    <Td>Dato 1</Td>
                    <Td>Dato 2</Td>
                  </tr>
                </tbody>
              </Table>
            </TableContainer>
          </CollapsibleContent>
        </Collapsible>
      </Collapsible>
    </>
  );
};

export default ConsejosEstatales;
