import React, { useRef } from 'react';
import styled from 'styled-components';

const VideoContainer = styled.video`
  width: 100%;
  height: 500px;
  margin-bottom: 2%;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;

const LanguageButton = styled.button`
  margin: 0 10px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
`;

const Frame = styled.iframe`
  width: 100%;
  margin-bottom: 2%;
`;

const Video = () => {
  const videoRef = useRef(null);

  const handleLanguageChange = (language) => {
    const video = videoRef.current;
    const tracks = video.textTracks;

    for (let i = 0; i < tracks.length; i++) {
      tracks[i].mode = tracks[i].language === language ? 'showing' : 'disabled';
    }
  };

  return (
    <>
      <Frame
        height='500'
        src='https://www.youtube.com/embed/jEU1lPe3bKg?si=QtC048c3lkrjMhXB'
        title='Consejo Nacional Forestal'
        frameborder='0'
        allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
        allowfullscreen
      ></Frame>

      <ButtonContainer>
        <LanguageButton onClick={() => handleLanguageChange('en')}>English</LanguageButton>
        <LanguageButton onClick={() => handleLanguageChange('es')}>Español</LanguageButton>
        <LanguageButton onClick={() => handleLanguageChange('fr')}>Français</LanguageButton>
        {/* Agrega más botones según los idiomas disponibles */}
      </ButtonContainer>

      <VideoContainer controls ref={videoRef}>
        <source src="ruta/al/video.mp4" type="video/mp4" />
        <track kind="subtitles" src="ruta/al/subtitles_en.vtt" srcLang="en" label="English" default />
        <track kind="subtitles" src="ruta/al/subtitles_es.vtt" srcLang="es" label="Español" />
        <track kind="subtitles" src="ruta/al/subtitles_fr.vtt" srcLang="fr" label="Français" />
        Your browser does not support the video tag.
      </VideoContainer>
    </>
  );
}

export default Video;
