import React from "react";
import Separator from "../components/Separator";
import Title from "../components/Title";
import ConsejoView from "../components/ConsejoView";
import styled from "styled-components";
import Collapsible from "./Collapsible";



const Reference = styled.p`
  display: block;
  width: 100%;
  font-size: 12px;
  color: var(--dark-grey);
  text-decoration: none;
  font-style: italic;

  @media screen and (min-width: 1024px) {
    font-size: 16px;
  }
`;
const DirectorioContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  justify-items: center;
  gap: 5px 0;
`;
const Text = styled.p`
  font-size: 9px;
  width: 100%;
  text-align: center;
  border-bottom: 1px solid #cdcbcb;
  padding: 2px 0;

  @media screen and (min-width: 767px) {
    font-size: 12px;
  }

  @media screen and (min-width: 1024px) {
    font-size: 16px;
  }
`;
const HeadText = styled.p`
  width: 100%;
  text-align: center;
  font-weight: 600;
  border-bottom: 1px solid var(--dark-grey);
  border-top: 1px solid var(--dark-grey);
`;
const ConsejoEstatal = ({ clave }) => {
  const estado = Estados.find((estado) => estado.shortname === clave);
const ConsejosEstatales = () => {

  return (
    <>
      <Title isHeader>Consejos Estatales Forestales</Title>
      <Separator />
      <Reference>
        Seleccione el estado del que desee visualizar información.
      </Reference>
      <ConsejoView />
      <Collapsible title={"Directorio"}>
          {estado.directorio && <h1> </h1>}
          <DirectorioContainer>
            {estado.directorio && (
              <>
                <HeadText>Integrante - COEF</HeadText>
                <HeadText>Nombre (s)</HeadText>
                <HeadText>Dependencia / Organización</HeadText>
              </>
            )}
            {estado.directorio?.map((dir) => (
              <React.Fragment key={dir.path}>
                <Text style={{ backgroundColor: '#f2f2f2' }}>{dir.integrante}</Text>
                <Text style={{ backgroundColor: '#f2f2f2' }}>{dir.nombre}</Text>
                <Text style={{ backgroundColor: '#f2f2f2' }}>{dir.dep}</Text>
              </React.Fragment>
            ))}
          </DirectorioContainer>
          </Collapsible>
    </>
  );
}; };


export default ConsejosEstatales;
