import React from "react";
import Separator from "../components/Separator";
import Title from "../components/Title";
import styled from "styled-components";
import Collapsible from "./Collapsible";
import infacuerdos from "../data/infacuerdos.json";

const Reference = styled.p`
  display: block;
  width: 100%;
  font-size: 12px;
  color: var(--dark-grey);
  text-decoration: none;
  font-style: italic;

  @media screen and (min-width: 1024px) {
    font-size: 16px;
  }
`;

const TableContainer = styled.div`
  width: 100%;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;

  th,
  td {
    padding: 10px;
    text-align: left;
    border-bottom: 1px solid #ddd;
  }

  th {
    background-color: #f2f2f2;
    font-weight: bold;
  }

  tr:hover {
    background-color: #f5f5f5;
  }
`;

const Th = styled.th`
  background-color: var(--light-grey);
  border: 1px solid var(--grey);
`;

const Td = styled.td`
  border: 1px solid var(--grey);
`;

const CollapsibleContent = styled.div`
  display: ${({ isOpen }) => (isOpen ? "block" : "none")};
`;

const ConsejosEstatales = () => {
  return (
    <>
      <Title isHeader>Informe de Acuerdos</Title>
      <Separator />
      <Reference>Seleccione el a√±o para ver los documentos disponibles.</Reference>

      <Collapsible title={"Informe de acuerdos 2023"}>
        <CollapsibleContent isOpen={true}>
          <TableContainer>
            <Table>
              <thead>
                <tr>
                  <Th>Trimestre</Th>
                  <Th>Documento</Th>
                </tr>
              </thead>
              <tbody>
                {infacuerdos["2023"].map((item, index) => (
                  <tr key={index}>
                    <Td>{item.trimestre}</Td>
                    <Td>
                      <a href={item.path} target="_blank" rel="noopener noreferrer">Archivo PDF</a>
                    </Td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </TableContainer>
        </CollapsibleContent>
      </Collapsible>

      <Collapsible title={"Informe de acuerdos 2024"}>
        <CollapsibleContent isOpen={true}>
          <TableContainer>
            <Table>
              <thead>
                <tr>
                  <Th>Columna 1</Th>
                  <Th>Columna 2</Th>
                </tr>
              </thead>
              <tbody>
                {infacuerdos["2024"].map((item, index) => (
                  <tr key={index}>
                    <Td>{item.columna1}</Td>
                    <Td>
                      <a href={item.path} target="_blank" rel="noopener noreferrer">Archivo PDF</a>
                    </Td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </TableContainer>
        </CollapsibleContent>
      </Collapsible>
    </>
  );
};

export default ConsejosEstatales;
