import React, { useState } from "react";
import Separator from "../components/Separator";
import Title from "../components/Title";
import ConsejoView from "../components/ConsejoView";
import styled from "styled-components";
import { FaAngleUp, FaAngleDown } from 'react-icons/fa';
import Collapsible from "./Collapsible";



const Reference = styled.p`
  display: block;
  width: 100%;
  font-size: 12px;
  color: var(--dark-grey);
  text-decoration: none;
  font-style: italic;

  @media screen and (min-width: 1024px) {
    font-size: 16px;
  }
`;

const TableContainer = styled.div`
  margin-top: 20px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const Th = styled.th`
  background-color: var(--light-grey);
  border: 1px solid var(--grey);
  padding: 8px;
  text-align: left;
`;

const Td = styled.td`
  border: 1px solid var(--grey);
  padding: 8px;
`;

const CollapsibleContainer = styled.div`
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-top: 20px;
`;

const CollapsibleHeader = styled.div`
  padding: 10px;
  background-color: #f9f9f9;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const CollapsibleTitle = styled.h2`
  margin: 0;
`;

const CollapsibleContent = styled.div`
  padding: 10px;
  display: ${({ isOpen }) => (isOpen ? "block" : "none")};
`;

const CollapsibleButton = styled.button`
  background-color: transparent;
  border: none;
  cursor: pointer;
  color: var(--dark-grey);
  font-size: 14px;
  text-decoration: underline;
  display: flex;
  align-items: center;
  margin-top: 8px;

  &:focus {
    outline: none;
  }
`;



const ConsejosEstatales = () => {
  const [isCollapsibleOpen, setIsCollapsibleOpen] = useState(false);

  const toggleCollapsible = () => {
    setIsCollapsibleOpen(!isCollapsibleOpen);
  };

  return (
    <>
      <Title isHeader>Consejos Estatales Forestales</Title>
      <Separator />
      <Reference>
        Seleccione el estado del que desee visualizar información.
      </Reference>
      <ConsejoView />
<Collapsible></Collapsible>
      <CollapsibleContainer>
        <CollapsibleHeader onClick={toggleCollapsible}>
          <CollapsibleTitle>Ver más información</CollapsibleTitle>
          <CollapsibleButton>
          {isCollapsibleOpen ? <FaAngleUp /> : <FaAngleDown />}
          </CollapsibleButton>
        </CollapsibleHeader>
        <CollapsibleContent isOpen={isCollapsibleOpen}>
          <TableContainer>
            <Table>
              <thead>
                <tr>
                  <Th>Encabezado de columna 1</Th>
                  <Th>Encabezado de columna 2</Th>
                  <Th>Encabezado de columna 3</Th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <Td>Dato 1</Td>
                  <Td>Dato 2</Td>
                  <Td>Dato 3</Td>
                </tr>
                {/* Puedes agregar más filas según sea necesario */}
              </tbody>
            </Table>
          </TableContainer>
        </CollapsibleContent>
      </CollapsibleContainer>
    </>
  );
};

export default ConsejosEstatales;
