import React, { useState } from 'react';
import styled from "styled-components";
import Separator from "../components/Separator";
import Reference from "../components/Reference";

const Wrapper = styled.div`
  width: 100%;
`;

const Description = styled.p`
  width: 100%;
  font-weight: 400;
  text-align: justify;

  @media screen and (max-width: 767px) {
    font-size: 16px;
  }

  @media screen and (min-width: 768px) and (max-width: 1023px) {
    font-size: 18px;
  }

  @media screen and (min-width: 1024px) {
    font-size: 24px;
  }
`;
const Title = styled.h1`
  width: 100%;

  @media screen and (max-width: 767px) {
  }

  @media screen and (min-width: 768px) and (max-width: 1023px) {
  }

  @media screen and (min-width: 1024px) {
    font-size: 32px;
  }
`;

const Light = styled.p`
  display: inline;
  font-weight: 300;
  font-size: 24px;

  @media screen and (min-width: 1024px) {
    font-size: 32px;
  }
`;

const Frame = styled.iframe`
  width: 100%;
  margin-bottom: 2%;
`;

const VideoContainer = styled.video`
  width: 100%;
  height: 500px;
  margin-bottom: 2%;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;

const LanguageButton = styled.button`
  margin: 0 10px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
`;

const Video = () => {
  const [language, setLanguage] = useState('en');

  const handleLanguageChange = (language) => {
    setLanguage(language);
  };

  const videoSources = {
    en: 'ruta/al/video_en.mp4',
    es: 'ruta/al/video_es.mp4',
    fr: 'ruta/al/video_fr.mp4',
    // Agrega más idiomas según sea necesario
  };

  return (
    <>
      <Frame
        height='500'
        src='https://www.youtube.com/embed/jEU1lPe3bKg?si=QtC048c3lkrjMhXB'
        title='Consejo Nacional Forestal'
        frameborder='0'
        allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
        allowfullscreen
      ></Frame>

      <ButtonContainer>
        <LanguageButton onClick={() => handleLanguageChange('en')}>English</LanguageButton>
        <LanguageButton onClick={() => handleLanguageChange('es')}>Español</LanguageButton>
        <LanguageButton onClick={() => handleLanguageChange('fr')}>Français</LanguageButton>
        {/* Agrega más botones según los idiomas disponibles */}
      </ButtonContainer>

      <VideoContainer controls>
        <source src={videoSources[language]} type="video/mp4" />
        Your browser does not support the video tag.
      </VideoContainer>
    </>
  );
};

const Index = () => {
  return (
    <>
      <Title id="index">
        Consejo Nacional Forestal <Light>(CONAF)</Light>
      </Title>
      <Separator />
      <Wrapper>
        <Description>
          Es un
          <strong> órgano de carácter consultivo y de asesoramiento</strong> en
          las materias que le señala la Ley General de Desarrollo Forestal
          Sustentable y en las que se le solicite su opinión. Funge como órgano
          de asesoría, supervisión, vigilancia, evaluación y seguimiento en la
          aplicación de los criterios e instrumentos de política forestal
          establecidos en la Ley General de Desarrollo Forestal Sustentable.
        </Description>
      </Wrapper>
      <Reference Color={"darkgrey"} href={"/marco-legal"}>
        Artículo 152 de la Ley General de Desarrollo Forestal Sustentable
      </Reference>
      <Video /> {/* Añade el componente Video aquí */}
    </>
  );
};

export default Index;
