import React, { useState } from 'react';
import styled from "styled-components";
import Separator from "../components/Separator";

const Title = styled.h1`
  width: 100%;

  @media screen and (min-width: 1024px) {
    font-size: 32px;
  }
`;

const Light = styled.p`
  display: inline;
  font-weight: 300;
  font-size: 24px;

  @media screen and (min-width: 1024px) {
    font-size: 32px;
  }
`;

const VideoContainer = styled.div`
  width: 100%;
  margin-bottom: 2%;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;

const LanguageButton = styled.button`
  margin: 0 10px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
`;

const Video = () => {
  const [language, setLanguage] = useState('en');

  const handleLanguageChange = (language) => {
    setLanguage(language);
  };

  const videoSources = {
    en: "https://www.youtube.com/embed/bX4hwZD3B1s?si=WTd_2srHK-OQl323",
    es: 'https://www.youtube.com/watch?v=VIDEO_ID_ES',
    fr: 'https://www.youtube.com/watch?v=VIDEO_ID_FR',
    // Agrega más idiomas según sea necesario
  };

  return (
    <>
      <Title id="vid">
        Video <Light>(CONAF)</Light>
      </Title>
      <Separator />
      <ButtonContainer>
        <LanguageButton onClick={() => handleLanguageChange('en')}>English</LanguageButton>
        <LanguageButton onClick={() => handleLanguageChange('es')}>Español</LanguageButton>
        <LanguageButton onClick={() => handleLanguageChange('fr')}>Français</LanguageButton>
        {/* Agrega más botones según los idiomas disponibles */}
      </ButtonContainer>

      <VideoContainer>
        <iframe
          width="100%"
          height="500px"
          src={videoSources[language].replace('watch?v=', 'embed/')}
          title="YouTube video player"
          frameborder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>
      </VideoContainer>
    </>
  );
};

export default Video;
