import styled from "styled-components";
import React from "react";
import Estados from "../data/Estados.json";
import { FaFilePdf } from "react-icons/fa";
import Collapsible from "./Collapsible";
import { Link } from "react-scroll";

const FloatTitle = styled(Link)`
  border-bottom: 1px dotted var(--dark-grey);
  font-weight: 600;
  font-size: 16px;
  margin: 10px 0;
  cursor: pointer;
  display: block;

  &:hover {
    color: var(--dark-yellow);
  }
  @media screen and (min-width: 1701px) and (max-width: 1920px) {
    font-size: 20px;
  }
`;
const FloatLink = styled(Link)`
  font-size: 7px;
  width: 50px;
  text-decoration: none;
  margin: 0;
  display: block;
  cursor: pointer;
  background-color: var(--wine);
  color: #fff;
  padding: 1px 0;
  text-align: center;
  border-radius: 0 10px 0 10px;

  &:hover {
    background-color: var(--dark-yellow);
  }
  @media screen and (min-width: 1701px) and (max-width: 1920px) {
    width: 30px;
    font-size: 8px;
  }
  @media screen and (min-width: 1920px) and (max-width: 2100px) {
    width: 40px;
    font-size: 10px;
  }
`;

const Main = styled.div`
  width: 100%;
  height: 100%;
`;
const Header = styled.div`
  width: 100%;
`;

const HeaderTitle = styled.h1`
  color: var(--green);
`;

const Year = styled.h1`
  color: #000;
`;

const ConsejoContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  justify-items: center;
  gap: 5px 0;
`;
const DirectorioContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  justify-items: center;
  gap: 5px 0;
`;
const ReglamentoContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  justify-items: center;
  gap: 5px 0;
`;

const Anchor = styled.a`
  width: 100%;
  text-align: center;
  border-bottom: 1px solid #cdcbcb;
  color: var(--red);
  cursor: pointer;
  padding: 2px 0;
`;

const Text = styled.p`
  font-size: 9px;
  width: 100%;
  text-align: center;
  border-bottom: 1px solid #cdcbcb;
  padding: 2px 0;

  @media screen and (min-width: 767px) {
    font-size: 12px;
  }

  @media screen and (min-width: 1024px) {
