import styled from "styled-components";
import { NavLink } from "react-router-dom";
import comites from "../data/Comites.json";
import Separator from "./Separator";
import Card from "./Card";
import Title from "./Title";
import Reference from "./Reference";

const StyledHomeComite = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
`;

const Subtitle = styled.h2``;

const Text = styled.p`
  height: 100%;
  text-align: justify;
  margin-bottom: 15px;
`;

const List = styled.nav`
  display: flex;
  flex-direction: column;
`;

const Item = styled(NavLink)`
  font-weight: 600;
  text-decoration: none;
  list-style: none;
  color: #000;

  &:hover {
    font-weight: 600;
  }
`;

const Lista = styled.ul`
  text-align: justify;
  padding: 0 20px;
  margin-left: 15px;

  @media screen and (max-width: 767px) {
    padding: 0 5px;
  }

  @media screen and (min-width: 768px) and (max-width: 1023px) {
  }

  @media screen and (min-width: 1024px) {
  }
`;

const ItemList = styled.li`
  text-align: justify;
  line-height: 1.6;

  @media screen and (max-width: 767px) {
    padding: 0 5px;
  }
`;

const Wrapper = styled.div`
  display: grid;
  width: 100%;
  height: 100%;
  align-items: center;
  padding-top: 10px;
  grid-template-columns: 35% 30% 35%;
  grid-template-rows: auto;
  grid-template-areas:
    "title title title"
    "subtitle text text"
    "subtitle2 text2 text2"
    "footer footer footer";

  &.secondary {
    grid-template-areas:
      "title title title"
      ". subtitle list"
      ". subtitle2 list"
      ". subtitle3 list"
      "footer footer footer";
  }

@media screen and (min-width: 767px) {
    grid-template-areas:
      "title subtitle text"
      "title subtitle2 text2"
      "footer footer footer";

    &.secondary {
      grid-template-areas:
        "title title title"
        "subtitle subtitle2 subtitle3"
        "footer footer footer";
    }

  @media screen and (min-width: 1024px) {
    grid-template-areas:
      "title subtitle text"
      "title subtitle2 text2"
      "footer footer footer";

    &.secondary {
      grid-template-areas:
        "title title title"
        "subtitle subtitle2 subtitle3"
        "footer footer footer";
    }
  }
`;

const SubTitle = styled.h4`
  font-weight: 300;
  text-align: center;
  border-radius: 6px;
  padding: 2px 0;
  grid-area: ${({ area }) =>
    (area === 2 && "subtitle2") || (area === 3 ? "subtitle3" : "subtitle")};

  color: ${({ Color }) =>
    (Color === "green" && "var(--green)") ||
    (Color === "darkgreen" && "var(--dark-green)") ||
    (Color === "yellow" && "var(--yellow)") ||
    (Color === "darkyellow" && "var(--dark-yellow)") ||
    (Color === "red" && "var(--red)") ||
    (Color === "darkred" && "var(--dark-red)") ||
    (Color === "grey" && "var(--grey)") ||
    (Color === "darkgrey" && "var(--dark-grey)") ||
    (Color === "wine" && "var(--wine)")};

  @media screen and (min-width: 1024px) {
    padding: 0 10px;
  }
`;

const Footer = styled.div`
  width 100%;
  display: flex;
  flex-direction: column; 
  align-items: center;
  grid-area: footer;
  font-size: 12px;
  margin-top: 10px;
  padding: 10px 20px;
  border-radius: 0 0 4px 4px;
  box-shadow: 0px 0px 5px rgba(0,0,0,0.2);
  background-color: ${({ Color }) =>
    (Color === "green" && "var(--green)") ||
    (Color === "darkgreen" && "var(--dark-green)") ||
    (Color === "yellow" && "var(--yellow)") ||
    (Color === "darkyellow" && "var(--dark-yellow)") ||
    (Color === "red" && "var(--red)") ||
    (Color === "darkred" && "var(--dark-red)") ||
    (Color === "grey" && "var(--grey)") ||
    (Color === "darkgrey" && "var(--dark-grey)") ||
    (Color === "wine" && "var(--wine)")};
  color: #fff;
`;

const RepresentacionesFooter = styled.div`
  width 100%;
  display: flex;
  flex-direction: column;
  grid-area: footer;
  padding: 10px;
  margin-top: 10px;
